// delete operation - delete rides / food establishments / shops
// update operation - food establishments

import React, { useEffect, useState } from "react";
import './Operations.css';
const url = "http://localhost:8000/rides"

const Operations = () => {

    const [rides, setRides] = useState([]);
    const [parks, setParks] = useState([]);

    const getRides = async () => {
        const response = await fetch(url);
        const json = await response.json();
        setRides(json);
    }

    const getParks = async () => {
        const response = await fetch(url);
        const json = await response.json();
        setParks(json);
    }

    useEffect(() => {
        getRides();
        getParks();
    }, []);

    return (
        <>
            <div className="rides-box-container">
                <div className="rides-text-container">
                    <h2>Global Operations Rides Database</h2>
                    <form>
                        <table id="rides">
                            <thead>
                                <tr>
                                <th>Ride Name</th>
                                <th>Park #</th>
                                <th>Thrill Level</th>
                                <th>Capacity</th>
                                <th>Delete</th>
                                </tr>
                            </thead>
                            <tbody>
                                {rides.map(ride => (
                                    <tr>
                                        <td>{ride.name}</td>
                                        <td>{ride.park_id}</td>
                                        <td>{ride.thrill_level}</td>
                                        <td>{ride.capacity}</td>
                                        <td><input type="checkbox"></input></td>
                                    </tr>
                                    ))}
                            </tbody>
                        </table>
                    <input type="submit" id="ridesubmit" value="Remove Ride(s)" />
                    </form>
                </div>
            </div>
            <div className="rides-box-container">
                <div className="rides-text-container">
                    <h2>Global Operations Rides Database</h2>
                    <form>
                        <table id="rides">
                            <thead>
                                <tr>
                                <th>Park #</th>
                                <th>City</th>
                                <th>Country</th>
                                </tr>
                            </thead>
                            <tbody>
                                {rides.map(ride => (
                                    <tr>
                                        <td>{ride.name}</td>
                                        <td>{ride.park_id}</td>
                                        <td>{ride.thrill_level}</td>
                                        <td>{ride.capacity}</td>
                                        <td><input type="checkbox"></input></td>
                                    </tr>
                                    ))}
                            </tbody>
                        </table>
                    <input type="submit" id="ridesubmit" value="Remove Ride(s)" />
                    </form>
                </div>
            </div>
        </>
    );
};

export default Operations;
