// insert operation - add new staff

import React, { useState } from "react";
import './AddOperations.css';
const staffUrl = "http://localhost:8000/staff"
const rideUrl = "http://localhost:8000/rides"

const AddStaff = () => {

    const [staffName, setStaffName] = useState("");
    const [role, setRole] = useState("");

    const [rideName, setRideName] = useState("");
    const [park, setPark] = useState("");
    const [thrillLevel, setThrill] = useState("");
    const [capacity, setCapacity] = useState("");

    const handleStaffSubmit = async (e) => {
        e.preventDefault();

        const body = { staffName, role };
        const response = await fetch(staffUrl, {
            method: "POST",
            headers: { "Content-Type": "application/json"},
            body: JSON.stringify(body)
        });

        console.log(response);
        alert("Employee successfully added!");

        // clear input values in form
        setStaffName('');
        setRole('');
    }

    const handleRideSubmit = async (e) => {
        e.preventDefault();

        const body = { rideName, park, thrillLevel, capacity };
        const response = await fetch(rideUrl, {
            method: "POST",
            headers: { "Content-Type": "application/json"},
            body: JSON.stringify(body)
        });

        console.log(response);
        alert("Ride successfully added!");

        // clear input values in form
        setRideName('');
        setPark('');
        setThrill('');
        setCapacity('');
    }

    return (
        <>
            <div className="box-container">
                <div className="text-container">
                    <h2>Register New Employees</h2>
                    <div className="name">
                        <form onSubmit={handleStaffSubmit}>
                            <label>Full Name: </label>
                                <input
                                    type="text"
                                    name="name"
                                    value={staffName}
                                    onChange={e => setStaffName(e.target.value)}
                                    required />
                            <div className="formspace"></div>
                            <label>Role: </label>
                                <input
                                    type="text"
                                    name="role"
                                    value={role}
                                    onChange={e => setRole(e.target.value)}
                                    required />
                            <div className="formspace"></div>
                            <input type="submit" value="Add Staff" />
                        </form>
                    </div>
                </div>

                <div className="text-container">
                    <h2>Add a New Ride</h2>
                    <div className="name">
                        <form onSubmit={handleRideSubmit}>
                            <label>Name: </label>
                                <input
                                    type="text"
                                    value={rideName}
                                    onChange={e => setRideName(e.target.value)}
                                    required />
                            <div className="formspace"></div>
                            <label>Park #: </label>
                                <input
                                    type="number"
                                    min="1"
                                    value={park}
                                    onChange={e => setPark(e.target.value)}
                                    required />
                            <div className="formspace"></div>
                            <label>Thrill Level: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="10"
                                    value={thrillLevel}
                                    onChange={e => setThrill(e.target.value)}
                                    required />
                            <div className="formspace"></div>
                            <label>Capacity: </label>
                                <input
                                    type="number"
                                    min="1"
                                    value={capacity}
                                    onChange={e => setCapacity(e.target.value)}
                                    required />
                            <div className="formspace"></div>
                            <input type="submit" value="Add Ride" />
                        </form>
                    </div>
