import React, { useState } from "react";
import './Updates.css';
const diningUrl = "http://localhost:8000/food"
const rideUrl = "http://localhost:8000/rides"


const Updates = () => {

    const [oldDiningName, setDiningOldName] = useState("");
    const [newDiningName, setDiningNewName] = useState("");
    const [newDiningParkID, setDiningNewParkID] = useState("");
    const [newDiningPrice, setDiningNewPrice] = useState("");

    const [oldRideName, setOldRideName] = useState("");
    const [newRideName, setNewRideName] = useState("");
    const [newRideParkID, setNewRideParkID] = useState("");
    const [newRideThrill, setNewRideThrill] = useState("");
    const [newRideCapacity, setNewRideCapacity] = useState("");

    const HandleDiningUpdate = async (e) => {
        e.preventDefault();

        const body = { oldDiningName, newDiningName, newDiningParkID, newDiningPrice };
        const response = await fetch(diningUrl, {
            method: "POST",
            headers: { "Content-Type": "application/json"},
            body: JSON.stringify(body)
        });

        console.log(response);
        alert("Restaurant successfully updated!");
        
        setDiningOldName('');
        setDiningNewName('');
        setDiningNewParkID('');
        setDiningNewPrice('');
    }

    const HandleRideUpdate = (e) => {
        // e.preventDefault();

        // const body = { oldRestaurantName, newRestaurantParkID, newRestaurantName,
        //                newRestaurantCuisine, newRestaurantPrice, newRestaurantSeats };
        // const response = await fetch(restaurantUrl, {
        //     method: "POST",
        //     headers: { "Content-Type": "application/json"},
        //     body: JSON.stringify(body)
        // });

        // console.log(response);
        // alert("Ride successfully updated!");
        // window.location.reload(false);

        // setOldRideName('');
        // setNewRideName('');
        // setNewRideParkID('');
        // setNewRideThrill('');
        // setNewRideCapacity('');
    }

    return (
        <>
            <div className = "box-container1">
                <div className = "text-container">
                    <h2>Update Dining Options</h2>
                    <div className="name">
                        <form onSubmit={HandleDiningUpdate}>
                            <label>Existing Dining Name: </label>
                                <input 
                                    type ="textbox"
                                    value={oldDiningName}
                                    onChange={e => setDiningOldName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>
                            <div className="formspace"></div>
                            <label>Name: </label>
                                <input
                                    type="text"
                                    value = {newDiningName}
                                    onChange = {e => setDiningNewName(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Park Id: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newDiningParkID}
                                    onChange = {e => setRestNewParkID(e.target.value)}
                                    required />


                            <div className="formspace"></div>
                            <label>Cuisine: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantCuisine}
                                    onChange = {e => setRestNewCuisine(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Price: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantPrice}
                                    onChange = {e => setRestNewPrice(e.target.value)}
                                    required />
                                    
                            <div className="formspace"></div>
                            <label>Seats: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantSeats}
                                    onChange = {e => setRestNewSeats(e.target.value)}
                                    required />

                            <input type="update" value="Update Restaurant" />
                        </form>                        
                    </div>

                    <br></br>
                    <h2>Update Ride Options</h2>
                    <div className="name">
                        <form onSubmit={HandleRideUpdate}>
                            <label>Existing Ride Name: </label>
                                <input 
                                    type ="textbox"
                                    value={oldRideName}
                                    onChange={e => setOldRideName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>
                            <div className="formspace"></div>
                            <label>Ride Name: </label>
                                <input
                                    type={"text"}
                                    value = {newRideName}
                                    onChange = {e => setNewRideName(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Park ID: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newRideParkID}
                                    onChange = {e => setNewRideParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Thrill Level: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="10"
                                    value = {newRideThrill}
                                    onChange = {e => setNewRideThrill(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Capacity: </label>
                                <input
                                    type="number"
                                    min="1"
                                    value = {newRideCapacity}
                                    onChange = {e => setNewRideCapacity(e.target.value)}
                                    required />
                                    
                            <input type="update" value="Update Restaurant" />
                        </form>                        
                    </div>
                        
                </div>
            </div>
            
        </>
    );
};

export default Updates;
