import React, { useEffect, useState } from "react";
import './BI.css';
const url = "http://localhost:8000/loyalty/"

const BI = () => {

    const [loyalty, setLoyalty] = useState([]);

    const getLoyalty = async (num) => {
        const response = await fetch(url + `/${num}`);
        const json = await response.json();
        setLoyalty(json);
    }

    useEffect(() => {
        getLoyalty();
    }, []);

    return (
        <>
            <div className="bi-page-container">
                <div className="header">
                        <h1>Business Intelligence Dashboard</h1>
                </div>
                <div className="ops-box-container">
                    <div className="left-text-container">
                        <h2>Customer Loyalty</h2>
                        <form onSubmit={getLoyalty}>
                        <input
                                    type="number"
                                    value={() => 0}
                                    onChange={e => num = e.target.value}
                                    required />
                        <input type="submit" value="Find Customers" />
                        </form>
                        <table id="customers">
                            <thead>
                                <tr>
                                <th>Guest #</th>
                                <th>Guest Name</th>
                                <th># of Visits Globally</th>
                                </tr>
                            </thead>
                            <tbody>
                                {loyalty.map(l => (
                                    <tr>
                                        <td>{l.guest_id}</td>
                                        <td>{l.name}</td>
                                        <td>{l.visits}</td>
                                    </tr>
                                    ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    );
};

export default BI;
