import React, { Fragment , useState} from "react";
import './EditFood.css';


const EditFood = ({food}) => {
    const [name, setName] = useState(food.name);
    const [park_id, setpark_id] = useState(food.park_id);
    const [thrill_level, setthrill_level] = useState(food.thrill_level);
    const [capacity, setcapacity] = useState(food.capacity);

    const updateName = async e => {
        e.preventDefault();
        try {
            const body = {name}; 
            const response = await fetch(`http://localhost:8000/food/${food.name}`, {
                method : "PUT",
                headers: {"Content-Type": "application/json" },
                body: JSON.stringify(body)
            });
