    });

    return (
        <>
            <div className="ops-box-container">
                <div className="left-text-container">
                    <h2>Hours</h2>
                        <p>Choose a park:   </p>
                        <select id="parkhours">
                            <option value="default">Park Number</option>
                            <option value="1">1</option>
                            <option value="2">2</option>
                            <option value="3">3</option>
                            <option value="4">4</option>
                            <option value="5">5</option>
                        </select>

                        <table id="hours">
                            <thead>
                            <tr>
                                <th>Park #</th>
                                <th>City</th>
                                <th>Country</th>
                                <th>Hours</th>
                            </tr>
                            </thead>
                            <tbody>
                            {parkInfos.map(parkInfo => (
                                                <tr>
                                                    <td>{parkInfo.id}</td>
                                                    <td>{parkInfo.city}</td>
                                                    <td>{parkInfo.country}</td>
                                                    <td>{parkInfo.hours}</td>
                                                </tr>
                                            ))}

                            </tbody>
                        </table>
                </div>
            </div>
        </>
    )
};

export default ParkInfo;
