import React, { useState } from "react";
import './Updates.css';

const Updates = ({ dining }) => {

    const [name, setName] = useState();
    const [parkid, setParkID] = useState();
    const [price, setPrice] = useState();

    const HandleDiningUpdate = async (e) => {
        e.preventDefault();

        const body = { name, parkid, price };
        const response = await fetch(`http://localhost:8000/food/${dining.name}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json"},
            body: JSON.stringify(body)
        });
    }

    return (
        <>
            <div className = "box-container1">
                <div className = "text-container">

                    <h2>Update Dining Options</h2>

                    <div className="name">
                        <form onSubmit={e => HandleDiningUpdate(e)}>
                            <label>Existing Dining Name: </label>
                                <input 
                                    type ="text"
                                    name="name"
                                    value={name}
                                    // onChange={e => setName(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Park Id: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {parkid}
                                    onChange = {e => setParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Price: </label>
                                <input
                                    type = "text"
                                    value = {price}
                                    onChange = {e => setPrice(e.target.value)}
                                    required />

                            <input type="submit" value="Update Dining" />
                        </form>                        
                    </div>                        
                </div>
            </div>          

        </>
    );
};

export default Updates;
