                                    type ="text"
                                    name="name"
                                    value={oldDiningName}
                                    onChange={e => setDiningOldName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>

                            <div className="formspace"></div>
                            <label>Park Id: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newDiningParkID}
