import React, { useState } from "react";
import './Updates.css';
const restaurantUrl = "http://localhost:8000/restaurant"
const fastFoodUrl = "http://localhost:8000/fastfood"


const Updates = () => {

    const [oldRestaurantName, setRestOldName] = useState("");
    const [newRestaurantParkID, setRestNewParkID] = useState("");
    const [newRestaurantName, setRestNewName] = useState("");
    const [newRestaurantCuisine, setRestNewCuisine] = useState("");
    const [newRestaurantPrice, setRestNewPrice] = useState("");
    const [newRestaurantSeats, setRestNewSeats] = useState("");

    const [oldFastFoodName, setFFOldName] = useState("");
    const [newFastFoodParkID, setFFNewParkID] = useState("");
    const [newFastFoodName, setFFNewName] = useState("");
    const [newFastFoodType, setFFNewType] = useState("");
    const [newFastFoodPrice, setFFNewPrice] = useState("");

    const [oldRideName, setOldRideName] = useState("");
    const [newRideName, setNewRideName] = useState("");
    const [newRideParkID, setNewRideParkID] = useState("");
    const [newRideThrill, setNewRideThrill] = useState("");
    const [newRideCapacity, setNewRideCapacity] = useState("");

    const HandleRestaurantUpdate = async (e) => {
        e.preventDefault();

        const body = { oldRestaurantName, newRestaurantParkID, newRestaurantName,
                       newRestaurantCuisine, newRestaurantPrice, newRestaurantSeats };
        const response = await fetch(restaurantUrl, {
            method: "POST"
        })

    }

    const HandleFastFoodUpdate = () => {

    }

    const HandleRideUpdate = () => {

    }

    return (
        <>
            <div className = "box-container1">
                <div className = "text-container">
                    <h2>Update Restaurant Options</h2>
                    <div className="name">
                        <form onSubmit={HandleRestaurantUpdate}>
                            <label>Existing Restaurant Name: </label>
                                <input 
                                    type ="textbox"
                                    value={oldRestaurantName}
                                    onChange={e => setRestOldName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>
                            <div className="formspace"></div>
                            <label>Park Id: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newRestaurantParkID}
                                    onChange = {e => setRestNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Name: </label>
                                <input
                                    type="text"
                                    value = {newRestaurantName}
                                    onChange = {e => setRestNewName(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Cuisine: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantCuisine}
                                    onChange = {e => setRestNewCuisine(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Price: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantPrice}
                                    onChange = {e => setRestNewPrice(e.target.value)}
                                    required />
                                    
                            <div className="formspace"></div>
                            <label>Seats: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantSeats}
                                    onChange = {e => setRestNewSeats(e.target.value)}
                                    required />

                            <input type="update" value="Update Restaurant" />
                        </form>                        
                    </div>

                    <br></br>
                    <h2>Update Fast Food Options</h2>
                    <div className="name">
                        <form onSubmit={HandleFastFoodUpdate}>
                            <label>Existing Restaurant Name: </label>
                                <input 
                                    type ="textbox"
                                    value={oldFastFoodName}
                                    onChange={e => setFFOldName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>
                            <div className="formspace"></div>
                            <label>Park Id: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newFastFoodParkID}
                                    onChange = {e => setFFNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Name: </label>
                                <input
                                    type = {"text"}
                                    value = {newFastFoodName}
                                    onChange = {e => setFFNewName(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Type: </label>
                                <input
                                    type = {"text"}
                                    value = {newFastFoodType}
                                    onChange = {e => setFFNewType(e.target.value)}
                                    required />
                                    
                            <div className="formspace"></div>
                            <label>Price: </label>
                                <input
                                    type = {"text"}
                                    value = {newFastFoodPrice}
                                    onChange = {e => setFFNewPrice(e.target.value)}
                                    required />

                            <input type="update" value="Update Restaurant" />
                        </form>
                    </div>

                    <br></br>
                    <h2>Update Ride Options</h2>
                    <div className="name">
                        <form onSubmit={HandleRideUpdate}>
                            <label>Existing Ride Name: </label>
                                <input 
                                    type ="textbox"
                                    value={oldRideName}
                                    onChange={e => setOldRideName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>
                            <div className="formspace"></div>
                            <label>Ride Name: </label>
                                <input
                                    type={"text"}
                                    value = {newRideName}
                                    onChange = {e => setNewRideName(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Park ID: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newRideParkID}
                                    onChange = {e => setNewRideParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Thrill Level: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="10"
                                    value = {newRideThrill}
                                    onChange = {e => setNewRideThrill(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Capacity: </label>
                                <input
                                    type="number"
                                    min="1"
                                    value = {newRideCapacity}
                                    onChange = {e => setNewRideCapacity(e.target.value)}
                                    required />
                                    
                            <input type="update" value="Update Restaurant" />
                        </form>                        
                    </div>
                        
                </div>
            </div>
            
        </>
    );
};

export default Updates;
