import React, { useState } from "react";
import './Updates.css';
const restaurantUrl = "http://localhost:8000/restaurant"
const fastFoodUrl = "http://localhost:8000/fastfood"


const Updates = () => {

    const [oldRestaurantName, setRestOldName] = useState("");
    const [newRestaurantParkID, setRestNewParkID] = useState("");
    const [newRestaurantName, setRestNewName] = useState("");
    const [newRestaurantCuisine, setRestNewCuisine] = useState("");
    const [newRestaurantPrice, setRestNewPrice] = useState("");
    const [newRestaurantSeats, setRestNewSeats] = useState("");

    const [oldFastFoodName, setFFOldName] = useState("");
    const [newFastFoodParkID, setFFNewParkID] = useState("");
    const [newFastFoodName, setFFNewName] = useState("");
    const [newFastFoodCuisine, setFFNewCuisine] = useState("");
    const [newFastFoodPrice, setFFNewPrice] = useState("");
    const [newFastFoodSeats, setFFNewSeats] = useState("");

    const HandleRestaurantUpdate = () => {

    }

    const HandleFastFoodUpdate = () => {

    }

    const HandleRideUpdate = () => {

    }

    return (
        <>
            <div className = "box-container1">
                <div className = "text-container">
                    <h2>Update Restaurant Options</h2>
                    <div className="name">
                        <form onSubmit={HandleRestaurantUpdate}>
                            <label>Existing Restaurant Name: </label>
                                <input 
                                    type ="textbox"
                                    value={oldRestaurantName}
                                    onChange={e => setrOldName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>
                            <div className="formspace"></div>
                            <label>Park Id: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Name: </label>
                                <input
                                    type="text"
                                    value = {newRestaurantName}
                                    onChange = {e => setNewName(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Cuisine: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantCuisine}
                                    onChange = {e => setNewCuisine(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Price: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantPrice}
                                    onChange = {e => setNewPrice(e.target.value)}
                                    required />
                                    
                            <div className="formspace"></div>
                            <label>Seats: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantSeats}
                                    onChange = {e => setNewSeats(e.target.value)}
                                    required />

                            <input type="update" value="Update Restaurant" />
                        </form>                        
                    </div>

                    <br></br>
                    <h2>Update Fast Food Options</h2>
                    <div className="name">
                        <form onSubmit={HandleFastFoodUpdate}>
                            <label>Existing Restaurant Name: </label>
                                <input 
                                    type ="textbox"
                                    value={oldRestaurantName}
                                    onChange={e => setOldName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>
                            <div className="formspace"></div>
                            <label>Park Id: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Name: </label>
                                <input
                                    type="text"
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Cuisine: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Price: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />
                                    
                            <div className="formspace"></div>
                            <label>Seats: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <input type="update" value="Update Restaurant" />
                        </form>

                    <br></br>
                    <h2>Update Ride Options</h2>
                    <div className="name">
                        <form onSubmit={HandleRestaurantUpdate}>
                            <label>Existing Restaurant Name: </label>
                                <input 
                                    type ="textbox"
                                    value={oldRestaurantName}
                                    onChange={e => setOldName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>
                            <div className="formspace"></div>
                            <label>Park Id: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Name: </label>
                                <input
                                    type="text"
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Cuisine: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Price: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />
                                    
                            <div className="formspace"></div>
                            <label>Seats: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <input type="update" value="Update Restaurant" />
                        </form>


                        
