import React, { useState } from "react";
import './Updates.css';
const restaurantUrl = "http://localhost:8000/restaurant"
const fastFoodUrl = "http://localhost:8000/fastfood"


const Updates = () => {

    const [oldRestaurantName, setOldName] = useState("");
    const [newRestaurantParkID, setNewParkID] = useState("");
    const [newRestaurantName, setNewName] = useState("");
    const [newRestaurantCuisine, setNewCuisine] = useState("");
    const [newRestaurantPrice, setNewPrice] = useState("");
    const [newRestaurantSeats, setNewSeats] = useState("");


    const HandleRestaurantUpdate = () => {

    }

    const HandleFastFoodUpdate = () => {

    }

    const HandleRideUpdate = () => {

    }

    return (
        <>
            <div className = "box-container1">
                <div className = "text-container">
                    <h2>Update Restaurant Options</h2>
                    <div className="name">
                        <form onSubmit={HandleRestaurantUpdate}>
                            <label>Existing Restaurant Name: </label>
                                <input 
                                    type ="textbox"
                                    value={oldRestaurantName}
                                    onChange={e => setOldName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>
                            <div className="formspace"></div>
                            <label>Park Id: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Name: </label>
                                <input
                                    type="text"
                                    value = {newRestaurantName}
                                    onChange = {e => setNewName(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Cuisine: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantCuisine}
                                    onChange = {e => setNewCuisine(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Price: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantPrice}
                                    onChange = {e => setNewPrice(e.target.value)}
                                    required />
                                    
                            <div className="formspace"></div>
                            <label>Seats: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantSeats}
                                    onChange = {e => setNewSeats(e.target.value)}
                                    required />

                            <input type="update" value="Update Restaurant" />
                        </form>                        
                    </div>

                    <br></br>
                    <h2>Update Fast Food Options</h2>
                    <div className="name">
                        <form onSubmit={HandleFastFoodUpdate}>
                            <label>Existing Restaurant Name: </label>
                                <input 
                                    type ="textbox"
                                    value={oldRestaurantName}
                                    onChange={e => setOldName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>
                            <div className="formspace"></div>
                            <label>Park Id: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Name: </label>
                                <input
                                    type="text"
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Cuisine: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Price: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />
                                    
                            <div className="formspace"></div>
                            <label>Seats: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <input type="update" value="Update Restaurant" />
                        </form>

                    <br></br>
                    <h2>Update Ride Options</h2>
                    <div className="name">
                        <form onSubmit={HandleRestaurantUpdate}>
                            <label>Existing Restaurant Name: </label>
                                <input 
                                    type ="textbox"
                                    value={oldRestaurantName}
                                    onChange={e => setOldName(e.target.value)}
                                    required />
                            <br></br>
                            <br></br>
                            <div className="formspace"></div>
                            <label>Park Id: </label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Name: </label>
                                <input
                                    type="text"
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Cuisine: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <div className="formspace"></div>
                            <label>Price: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />
                                    
                            <div className="formspace"></div>
                            <label>Seats: </label>
                                <input
                                    type = {"text"}
                                    value = {newRestaurantParkID}
                                    onChange = {e => setNewParkID(e.target.value)}
                                    required />

                            <input type="update" value="Update Restaurant" />
                        </form>


                        
                    </div>
                        
                    </div>
                </div>
            
            </div>
        </>
    );
};

export default Updates;
