import React, { Fragment , useState} from "react";
import './EditRides.css';


const EditRide = ({ride}) => {
    const [name, setName] = useState(ride.name);
    const [park_id, setpark_id] = useState(ride.park_id);
    const [thrill_level, setthrill_level] = useState(ride.thrill_level);
    const [capacity, setcapacity] = useState(ride.capacity);

    const updateName = async e => {
        e.prev
        try {
            
        } catch (error) {
            console.error(err.message);
        }
    }


    return (
        <Fragment>
            <button 
                type="button" 
                class="btn btn-warning" 
                data-toggle="modal" 
                data-target={`#id${ride.name}`}>
            Edit
            </button>

            <div class="modal" id={`id${ride.name}`}>
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h4 class="modal-title">Edit Ride</h4>
                            <button 
                                type="button" 
                                class="close" 
                                data-dismiss="modal">&times;
                            </button>
                        </div>

                <div class="modal-body">
                    <label>Name:</label>
                    <input type="text" className="form-control" value={name} onChange={e => setName(e.target.value)}/>

                    <label>Park ID:</label>
                    <input type="text" className="form-control" value={park_id} onChange={e => setName(e.target.value)}/>

                    <label>Thrill Level:</label>
                    <input type="text" className="form-control" value={thrill_level} onChange={e => setName(e.target.value)}/>

                    <label>Capacity:</label>
                    <input type="text" className="form-control" value={capacity} onChange={e => setName(e.target.value)}/>
                </div>

                <div class="modal-footer">
                    <button 
                        type="button" 
                        class="btn btn-warning" 
                        data-dismiss="modal"
                        onClick = {e => updateName(e)}>Edit</button>
                    <button 
                        type="button" 
                        class="btn btn-danger" 
                        data-dismiss="modal">Close</button>
                </div>

                </div>
            </div>
            </div>
        </Fragment>
    )
}

export default EditRide;
